/// status : 200

class CategoriesResponse {
  CategoriesResponse({
      this.materialCategories, 
      this.status,});

  CategoriesResponse.fromJson(dynamic json) {
    if (json['material_categories'] != null) {
      materialCategories = [];
      json['material_categories'].forEach((v) {
        materialCategories?.add(MaterialCategories.fromJson(v));
      });
    }
    status = json['status'];
  }
  List<MaterialCategories>? materialCategories;
  num? status;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    if (materialCategories != null) {
      map['material_categories'] = materialCategories?.map((v) => v.toJson()).toList();
    }
    map['status'] = status;
    return map;
  }

}

